    msg.a0 = msg.magnitudes[0];

    var maxshow = 0;
    if (msg.mp_pairs.length < maxHarmonicDisplay) maxshow = msg.mp_pairs.length;
    else maxshow = maxHarmonicDisplay;

    var datavalues = new Array(maxshow);
    var xlabels = new Array(maxshow);
    var titlelabel = "";

    //for (var i=0; i< msg.mp_pairs.length; i++)
    for (var i=0; i< maxshow; i++)
    {
        //Check the magnitude axis scaling (y-axis)
        if ("linear" == fft_log_magnitude_flag)
        {
            titlelabel = " (cm)";
            //i wrote the cweird looking logic below to force a similar comparison
            if ((0 == i) & ("false" == fft_include_a0_in_mags)) //null the DC (a0) value
                datavalues[i] = null; //it's easier just to do this
            else
            {
                if ("true" == fft_PowerSpectrum)
                    datavalues[i] = 1000000*msg.magnitudes[i]*msg.magnitudes[i]; //in cm^2x10^-6
                else
                    datavalues[i] = (Math.abs(msg.magnitudes[i])).toPrecision(3); //in cm
            }
            if (0 != i)
                powsum = powsum + msg.magnitudes[i]*msg.magnitudes[i]
        }
        else if ("log" == fft_log_magnitude_flag)
        {
            titlelabel = " (dBcm)";
