//This function doesn't care what type of object is in the buffer
//This function buffers whatever is in msg.payload
//set msg.setNewBufferSize to a 2^N number for a new buffer size

const default_buffer_size = 2; //two is arbitrary and conservative

var thebuffer = context.get('buffer')||[];
var buffsize = context.get('bufferSize')||default_buffer_size;

//only enter if() the buffer size needs a reset
if ((thebuffer.length != buffsize)||(undefined != msg.setNewBufferSize))
{
    if (undefined != msg.setNewBufferSize)
    {
        if (Number.isFinite(msg.setNewBufferSize))
        {
            buffsize = msg.setNewBufferSize;
        }
        else
            buffsize = default_buffer_size; //failure
    }
    else if (thebuffer.length != buffsize)
    {
        buffsize = default_buffer_size; //buffer size was reset asynchronously 
    } else
    {
        buffsize = default_buffer_size; //how'd we even get here?
    }

    //check for +ve power of two (i.e., at least two)
    if ((Math.log(buffsize)/Math.log(2) != 
            Math.floor(Math.log(buffsize)/Math.log(2)) )
            || (2>buffsize))
    {
        //reject this size, get out.
        node.warn({"text":"Buffer must be power of two (2^N)"});
        return null;
    }

    //at this point, everything should be OK with the new buffer size
    thebuffer = Array(buffsize);//cross fingers for garbage collection
    thebuffer.fill(0);
    context.set('bufferSize',buffsize); //make sure it knows
    context.set('buffer',thebuffer);  //store newly-sized, zeroed buffer
    node.status({"text":"New buffer size: "+buffsize});
    return null;  //throw away buffer-changing packets
}

//By here, the only payloads should be elements for the buffer
thebuffer.push(msg.payload);  //put in the back of the line
while (thebuffer.length > buffsize)
{
    thebuffer.shift(); //remove the front of the line
}


    msg.payload = thebuffer;
    msg.topic = "Entire Buffer size "+thebuffer.length+" released";
    node.status({"text":"last buffer size: "+thebuffer.length});


context.set('buffer',thebuffer);

