        msg.payload = Number( (3*(Math.pow(h2,2)-Math.pow(h1,2))
                             - Math.pow(discrim,0.5) )
                           /(6*(h2-h1))
                  ).toFixed(1);
        flow.set("last_calculated_offset",msg.payload);
        msg.topic = "calculated depth offset (cone section model) in centimetres"
    }
