var KELVIN_CELCIUS_OFFSET = 273.16;

//x is the length of the datasheet array
var xlength = 0;
if (null !== msg.datasheet)
    if (null !== msg.datasheet.data)
        if (null !== msg.datasheet.data.length)
            xlength = msg.datasheet.data.length;
if (xlength < 3) return null; //wtf?  too few points

//this is the RAW thermistor resistance calculated from ADC value  
msg.RTmeasured = msg.payload; //in ohms;
//rtr25_ratio = ( resistance / nominal or measured resistance of THERMISTOR at 25C )
if ("Reference"==msg.topic)
    msg.normed_resistance = msg.payload/10000.0;//nominal
else
    msg.normed_resistance = msg.payload/10000.0;//nominal


//look through datasheet values until the measurement 
//is within a local domain of datasheet points 
//(i.e. find which 3 nominal datasheet points to use for calculations)
var count = 0;
//the 1st less-than comparison in the while only works if data is sorted ascending for an NTC thermistor
while ( (msg.normed_resistance < msg.datasheet.data[count+1].rtr25_ratio) && (count+2 < xlength) ) 
{
    count+=1;
}

//this extra step is for HUMAN (my) clarity
var h_minus = count;
var h_zero = count+1;
var h_plus = count+2;

//three function points from datasheet
var fmh = parseFloat(msg.datasheet.data[h_minus].rtr25_ratio);   //f(-h)
var fz = parseFloat(msg.datasheet.data[h_zero].rtr25_ratio);  //f(0)
var fph = parseFloat(msg.datasheet.data[h_plus].rtr25_ratio); //f(+h)
msg.fmh = fmh; msg.fz = fz; msg.fph = fph; //make a copy in the msg body

//store the calculated matrix values in the msg object
msg.matrixA = [   [1.0,Math.log(fmh),Math.pow(Math.log(fmh),3)],
            [1.0,Math.log(fz),Math.pow(Math.log(fz),3)],
            [1.0,Math.log(fph),Math.pow(Math.log(fph),3)]
        ];
        //need temperature in KELVIN for eqn
msg.matrixB = [   1.0/(parseFloat(msg.datasheet.data[h_minus].tempC) +KELVIN_CELCIUS_OFFSET), 
            1.0/(parseFloat(msg.datasheet.data[h_zero].tempC) +KELVIN_CELCIUS_OFFSET), 
            1.0/(parseFloat(msg.datasheet.data[h_plus].tempC) +KELVIN_CELCIUS_OFFSET)
        ];

var goodMath = context.global.get('numericjs');

//there is only a solution of the determinant is non-zero
msg.det = goodMath.det(msg.matrixA);
if (0!==msg.det) 
{
    //solution is a 3x1 matrix (product of 3x3 and 3x1 matrix)
    msg.k = goodMath.dotMV(goodMath.inv(msg.matrixA),msg.matrixB); //the solution matrix in ohms
    msg.SH_calculated_Tt = 1.0/(msg.k[0] + msg.k[1]*Math.log(msg.normed_resistance) + msg.k[2]*Math.pow(Math.log(msg.normed_resistance), 3) );
